                        placeTrunk(pos, config, rand, changedBlocks, worldIn, mainmutable, boundsIn);
                        placeTrunk(pos, config, rand, changedBlocks, worldIn, mainmutable2, boundsIn);
                        placeTrunk(pos, config, rand, changedBlocks, worldIn, mainmutable3, boundsIn);
                        placeTrunk(pos, config, rand, changedBlocks, worldIn, mainmutable4, boundsIn);
                        placeTrunk(pos, config, rand, changedBlocks, worldIn, mainmutable5, boundsIn);
